import * as React from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import Slide from '@mui/material/Slide';
import DoDisturbOnOutlinedIcon from '@mui/icons-material/DoDisturbOnOutlined';

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

export default function AlertDialog({title, text}) {
  const [open, setOpen] = React.useState(true);



  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>

      <Dialog
        open={open}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleClose}
        aria-describedby="alert-dialog-slide-description"
        // fullWidth={true}
        sx={{Width: 900, textAlign: "center"}}
      >
        <DialogTitle >{title}</DialogTitle>
        <DialogContent>
          <DoDisturbOnOutlinedIcon color="error" fontSize="large"/>
          <DialogContentText id="alert-dialog-slide-description">
            {text}
          </DialogContentText>
        </DialogContent>
        <DialogActions sx={{bgcolor: "gray"}}>
          <Button onClick={handleClose} sx={{bgcolor: "blue"}}>Yes</Button>
          <Button onClick={handleClose} sx={{bgcolor: "#ff9100"}}>No</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
